package pratica4OO_Q3;

public class funcionario {

String nome;
double rg;


public funcionario(String nome, double rg) {
	super();
	this.nome = nome;
	this.rg = rg;
}


public funcionario() {
	// TODO Auto-generated constructor stub
}


public String getNome() {
	return nome;
}


public void setNome(String nome) {
	this.nome = nome;
}


public double getRg() {
	return rg;
}


public void setRg(double rg) {
	this.rg = rg;
}








}
